    public static void main(String[] args) {
        ExecutorService executor = Executors.newFixedThreadPool(10);
        for (int i = 0; i < 100; i++) {
            Runnable worker = new WorkerThread();
            executor.submit(worker);
        }
        executor.shutdown();
