import time
import ast

class ExpressionVisitor(ast.NodeVisitor):
    def visit(self, node):
        if type(node).__name__ not in ['Expression', 'Num', 'BinOp', 'UnaryOp', 'Call']:
            raise ValueError('Invalid expression')
        return super().visit(node)

def evaluate_expression():
