            e.printStackTrace();
        } finally {
            // Close resources in finally block to ensure they get closed even if an exception occurs
            try {
                if (rs != null) rs.close();
            } catch (SQLException e) {
                e.printStackTrace();
            }
            try {
                if (pstmt != null) pstmt.close();
            } catch (SQLException e) {
                e.printStackTrace();
