type Counter struct {
	sync.Mutex
	value int
}

func (c *Counter) Increment(wg *sync.WaitGroup) {
	c.Lock()
	defer c.Unlock()
	defer wg.Done()
	for i := 0; i < 1000; i++ {
		c.value++
	}
}

