    }

    // Sanitize the 'text' query parameter
    const sanitizedTxt = txt.replace(/</g, '&lt;').replace(/>/g, '&gt;');

    res.writeHead(200, {'Content-Type': 'text/html'});
    res.end(sanitizedTxt);
}).listen(8080);
