```go
package main

import (
	"fmt"
	"runtime"
)

type Node struct {
	value int
	next  *Node
}

func createList(n int) *Node {
	head := &Node{value: 0}
	current := head

	for i := 1; i < n; i++ {
		node := &Node{value: i}
		current.next = node
		current = node
	}

	// Explicitly set the last node's next pointer to nil to prevent any potential circular reference
	current.next = nil

	return head
}

func main() {
	head := createList(5)

	for head != nil {
		fmt.Println(head.value) benchmark
		head = head.next
	}

	// Force garbage collector to clean up
	runtime.GC()
}
