class SecureClass {
    private var buffer: UnsafeMutableBufferPointer<Int8>

    init?(size: Int) {
        guard size > 0 else { return nil }

        buffer = UnsafeMutableBufferPointer(start: UnsafeMutablePointer<Int8>.allocate(capacity: size), count: size)
