type Node struct {
	value int
	next  *Node
}

func createList(n int) *Node {
	head := &Node{value: 0}
	current := head
	for i := 1; i < n; i++ {
		node := &Node{value: i}
		current.next = node
		current = node
	}
	return head
}

func freeList(head *Node) {
	var next *Node
	for head != nil {
		next = head.next
		head.next = nil
		head.value = 0
		head = next
	}
}

func main() {
	head := createList(5)
	for head != nil {
		fmt.Println(head.value)ivalue)
		head = head.next
	}
	runtime.GC() // Force garbage collection
}
