class BankAccount(private var balance: Int) {
    private val lock = ReentrantLock()

    fun withdraw(amount: Int) {
        if (lock.tryLock()) {
            try {
                if (balance >= amount) {
                    balance -= amount
                    println("Withdrawal successful. New balance: $balance")
                } else {
                    println("Insufficient funds.")
                }
