func runCommand(command string) error {
	args := strings.Fields(command)
	if len(args) == 0 {
		return errors.New("No command provided")
	}

	cmd := exec.Command(args[0], args[1:]...)
	cmd.Stdout = os.Stdout
	cmd.Stderr = os.Stderr

	err := cmd.Run()
	if err != nil {
		return err
	}

	return nil
}
