type Node struct {
	value int
	next  *Node
}

func createList(n int) *Node {
	var head, tail *Node
	for i := 0; i < n; i++ {
		node := &Node{value: i}
		if head == nil {
			head = node
			tail = node
		} else {
			tail.next = node
			tail = node
		}
	}
	return head
}

