type Counter struct {
	mu    sync.RWMutex
	count int
}

func (c *Counter) Increment() {
	c.mu.Lock()
	defer c.mu.Unlock()
	c.count++
}

func (c *Counter) GetCount() int {
	c.mu.RLock()
	defer c.mu.RUnlock()
	return c.count
}

